func New(site string, siteKey string, proxy *url.URL) (*ReCaptchaV3, error) {
	if proxy != nil && !yp.IsSsl(proxy) {
		return nil, yp.ErrProxyMustBeSSL
	}

	recaptcha := ReCaptchaV3{site: site, siteKey: siteKey, proxy: proxy}
	client := recaptcha.getHttpClient()

	siteEncoded := base64.StdEncoding.EncodeToString([]byte(site))
	url := fmt.Sprintf("https://www.google.com/recaptcha/api2/anchor?ar=1&k=%s&co=%s&hl=en&type=image&theme=light&size=invisible&badge=bottomright", siteKey, siteEncoded)
	resp, err := client.Get(url)
	if err != nil {
		return nil, err
	}

	respBody, err := io.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	re := regexp.MustCompile(`<input[^>]*id="recaptcha-token"[^>]*value="([^"]*)"`)

	match := re.FindStringSubmatch(string(respBody))
	if match == nil || len(match) < 1 {
		return nil, fmt.Errorf("no token was found in anchor response (it must be wrong site or siteKey)")
	}

	recaptcha.token = match[1]
	err = recaptcha.Reload(ValidateData)
	if err != nil {
		return nil, err
	}

	return &recaptcha, nil
}

func Bypass(site string, siteKey string, proxy *url.URL) (*ReCaptchaV3, error) {
	return nil, fmt.Errorf("unimplemented")
}

func (r *ReCaptchaV3) getHttpClient() http.Client {
	client := http.Client{}
	if r.proxy != nil {
		client.Transport = &http.Transport{Proxy: http.ProxyURL(r.proxy)}
	}

	return client
}

func (r *ReCaptchaV3) Reload(data string) error {
	client := r.getHttpClient()

	siteEncoded := base64.StdEncoding.EncodeToString([]byte(r.site))
	body := fmt.Sprintf("reason=q&c=%s&k=%s&co=%s&hl=en&size=invisible&bq=%s", r.token, r.siteKey, siteEncoded, data)
	request, err := http.NewRequest(http.MethodPost, "https://www.google.com/recaptcha/api2/reload", bytes.NewReader([]byte(body)))
	if err != nil {
		return err
	}

	request.Header.Set("Content-Type", "application/x-www-form-urlencoded")

	resp, err := client.Do(request)
	if err != nil {
		return err
	}
	respBody, err := io.ReadAll(resp.Body)
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	if resp.StatusCode != 200 {
		return fmt.Errorf("%s: %s", resp.Status, string(respBody))
	}

	var parsedResp []interface{}
	err = json.Unmarshal([]byte(strings.Split(string(respBody), "\n")[1]), &parsedResp)
	if err != nil {
		return err
	}

	r.token = parsedResp[1].(string)

	return nil
}

func (r *ReCaptchaV3) GetToken() string {
	return r.token
}
